import React, { Component } from "react";
import Logo from "./Logo";
import AlertInfo from "../Thongbao/AlertInfo";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { AuthUserContext } from "../Session";

const HeaderBase = () => (
  <AuthUserContext.Consumer>
    {authUser =>
      authUser ? <NavigationAuth authUser={authUser} /> : <NavigationNonAuth />
    }
  </AuthUserContext.Consumer>
);

const NavigationAuth = ({ authUser }) => (
  <>
    <li className="nav-item dropdown u-pro">
      <a
        className="nav-link dropdown-toggle waves-effect waves-dark profile-pic"
        href="javascript:avoid(0)"
        data-toggle="dropdown"
        aria-haspopup="true"
        aria-expanded="false"
      >
        <img src="../assets/images/users/1.jpg" alt="user" />{" "}
        <span className="hidden-md-down">
          THTV &nbsp;
          <br />
        </span>{" "}
      </a>
    </li>
    <li className="nav-item dropdown u-pro">
      <Link to="/sign-out" className="nav-link fa fa-sign-out">
        Sign Out
      </Link>
    </li>
  </>
);

const NavigationNonAuth = () => (
  <>
    <li className="nav-item dropdown u-pro">
      <Link to="/sign-up" className="nav-link">
        Sign Up{" "}
      </Link>
    </li>

    <li className="nav-item dropdown u-pro">
      <Link to="/sign-in" className="nav-link">
        Sign In{" "}
      </Link>
    </li>
  </>
);
class Header extends Component {
  showdata = () => {
    if (this.props.trangthaithongbao === true) {
      return <AlertInfo />;
    }
  };

  render() {
    return (

      <header className="topbar">
        {this.showdata()}
        <ul class="nav nav-tabs" id="navId">
            <li class="nav-item">
                <a href="#tab1Id" class="nav-link active">Active</a>
            </li>
            <li class="nav-item dropdown">
                <a class="nav-link dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Dropdown</a>
                <div class="dropdown-menu">
                    <a class="dropdown-item" href="#tab2Id">Action</a>
                    <a class="dropdown-item" href="#tab3Id">Another action</a>
                    <div class="dropdown-divider"></div>
                    <a class="dropdown-item" href="#tab4Id">Action</a>
                </div>
            </li>
            <li class="nav-item">
                <a href="#tab5Id" class="nav-link">Another link</a>
            </li>
            <li class="nav-item">
                <a href="#" class="nav-link disabled">Disabled</a>
            </li>
        </ul>

        <div class="tab-content">
            <div class="tab-pane fade show active" id="tab1Id" role="tabpanel"></div>
            <div class="tab-pane fade" id="tab2Id" role="tabpanel"></div>
            <div class="tab-pane fade" id="tab3Id" role="tabpanel"></div>
            <div class="tab-pane fade" id="tab4Id" role="tabpanel"></div>
            <div class="tab-pane fade" id="tab5Id" role="tabpanel"></div>
        </div>
        {/* <nav className="navbar top-navbar navbar-expand-md navbar-light">
          <Logo />
          <div className="navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item">
                {" "}
                <a
                  className="nav-link nav-toggler hidden-md-up waves-effect waves-dark"
                  href="javascript:avoid(0)"
                >
                  <i className="fa fa-bars" />
                </a>{" "}
              </li>
              <li className="nav-item hidden-xs-down search-box">
                {" "}
                <a
                  className="nav-link hidden-sm-down waves-effect waves-dark"
                  href="javascript:avoid(0)"
                >
                  <i className="fa fa-search" />
                </a>
                <form className="app-search">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Search & enter"
                  />{" "}
                  <a className="srh-btn" href="javascript:avoid(0)">
                    <i className="fa fa-times" />
                  </a>
                </form>
              </li>
            </ul>
            <ul className="navbar-nav my-lg-0">
              <HeaderBase />
            </ul>
          </div>
        </nav> */}
      </header>
    );
  }
}
const mapStateToProps = (state, ownProps) => {
  return {
    trangthaithongbao: state.Thongbao.trangthai
  };
};
export default connect(mapStateToProps)(Header);
