import React, { Component } from "react";
import Logo from "./Logo";
import AlertInfo from "../Thongbao/AlertInfo";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { AuthUserContext } from "../Session";

const HeaderBase = () => (
  <AuthUserContext.Consumer>
    {authUser =>
      authUser ? <NavigationAuth authUser={authUser} /> : <NavigationNonAuth />
    }
  </AuthUserContext.Consumer>
);

const NavigationAuth = ({ authUser }) => (
  <>
    <li className="nav-item dropdown u-pro">
      <a
        className="nav-link dropdown-toggle waves-effect waves-dark profile-pic"
        href="javascript:avoid(0)"
        data-toggle="dropdown"
        aria-haspopup="true"
        aria-expanded="false"
      >
        <img src="../assets/images/users/1.jpg" alt="user" />{" "}
        <span className="hidden-md-down">
          THTV &nbsp;
          <br />
        </span>{" "}
      </a>
    </li>
    <li className="nav-item dropdown u-pro">
      <Link to="/sign-out" className="nav-link fa fa-sign-out">
        Sign Out
      </Link>
    </li>
  </>
);

const NavigationNonAuth = () => (
  <>
    <li className="nav-item dropdown u-pro">
      <Link to="/sign-up" className="nav-link">
        Sign Up{" "}
      </Link>
    </li>

    <li className="nav-item dropdown u-pro">
      <Link to="/sign-in" className="nav-link">
        Sign In{" "}
      </Link>
    </li>
  </>
);
class Header extends Component {
  showdata = () => {
    if (this.props.trangthaithongbao === true) {
      return <AlertInfo />;
    }
  };

  render() {
    return (

      <header className="topbar">
        {this.showdata()}
        <nav class="navbar navbar-expand-sm navbar-dark bg-primary">
            <a class="navbar-brand" href="#">Navbar</a>
            <button class="navbar-toggler d-lg-none" type="button" data-toggle="collapse" data-target="#collapsibleNavId" aria-controls="collapsibleNavId"
                aria-expanded="false" aria-label="Toggle navigation"></button>
            <div class="collapse navbar-collapse" id="collapsibleNavId">
                <ul class="navbar-nav mr-auto mt-2 mt-lg-0">
                    <li class="nav-item active">
                        <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" href="#">Link</a>
                    </li>
                    <li class="nav-item dropdown">
                        <a class="nav-link dropdown-toggle" href="#" id="dropdownId" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">Dropdown</a>
                        <div class="dropdown-menu" aria-labelledby="dropdownId">
                            <a class="dropdown-item" href="#">Action 1</a>
                            <a class="dropdown-item" href="#">Action 2</a>
                        </div>
                    </li>
                </ul>
                <form class="form-inline my-2 my-lg-0">
                    <input class="form-control mr-sm-2" type="text" placeholder="Search" />
                    <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                </form>
            </div>
        </nav>
        {/* <nav className="navbar top-navbar navbar-expand-md navbar-light">
          <Logo />
          <div className="navbar-collapse">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item">
                {" "}
                <a
                  className="nav-link nav-toggler hidden-md-up waves-effect waves-dark"
                  href="javascript:avoid(0)"
                >
                  <i className="fa fa-bars" />
                </a>{" "}
              </li>
              <li className="nav-item hidden-xs-down search-box">
                {" "}
                <a
                  className="nav-link hidden-sm-down waves-effect waves-dark"
                  href="javascript:avoid(0)"
                >
                  <i className="fa fa-search" />
                </a>
                <form className="app-search">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Search & enter"
                  />{" "}
                  <a className="srh-btn" href="javascript:avoid(0)">
                    <i className="fa fa-times" />
                  </a>
                </form>
              </li>
            </ul>
            <ul className="navbar-nav my-lg-0">
              <HeaderBase />
            </ul>
          </div>
        </nav> */}
      </header>
    );
  }
}
const mapStateToProps = (state, ownProps) => {
  return {
    trangthaithongbao: state.Thongbao.trangthai
  };
};
export default connect(mapStateToProps)(Header);
